import React from 'react'
import { useState } from 'react';

const Content = () => {

  const [items, setItems] = useState([
    {
      id: 1,
      item: "item 01",
      checked: false 
    },
    {
      id: 2,
      item: "item 02",
      checked: true 
    },
    {
      id: 3,
      item: "item 03",
      checked: false 
    }
  ]);

return (
    <main>
      <ul>
        {items.map((item) => {
          <li className='items' key={item.id}>
            <input
              type='checkbox'
              checked={item.checked}
            />
            <label>{item.item}</label>
            <button>Delete</button>

          </li>
        })}
      </ul> 
    </main>
  )
}

export default Content
